import React from 'react';
import Authentication from '../Authentication/Authentication';
import {GETUSERCOLLECTION,USERID,TAG,TOKEN,CREATECOLLECTION} from '../../url';
import axios from 'axios';
import $ from 'jquery'
import {Link} from 'react-router-dom'
import { ToastContainer, toast,cssTransition} from 'react-toastify';
import ActivityLoader from 'react-loader-spinner';
const Zoom = cssTransition({
enter: 'zoomIn',
exit: 'zoomOut',
duration: 750,
});
const HEADER = {
headers: {
  'Content-Type': 'application/json;charset=UTF-8',
  'Authorization':"Bearer " + localStorage.getItem('planetshare_web_token'),
}
};
class ImageCollection extends React.Component
{
  state={not_found:false,loading:false,loading_msg:'Please Wait',collection_list:[]}
  componentDidMount()
  {
  $(".cat_item .box .price_info a.wish.gig_ac").on("click", function(e) {
    $(this).closest('.box').addClass('open');
    e.stopPropagation()
  });
  $(document).on("click", function(e) {
    if ($(e.target).is(".box") === false) {
      $(".box").removeClass("open");
    }
  });

this.getData();
  }
  getData=()=>{
    axios.post(GETUSERCOLLECTION,{user_id:USERID,collection_type:'image',tag:TAG},HEADER).then((res)=>{
      if(res.data.success==1)
      {
        var response=res.data;
        this.setState({collection_list:response.data})
          setTimeout(()=>this.setState({loading:true}),1000)
      }
    }).catch((error)=>{

    })
  }
  createNewCollection=()=>{
    var coll_name=$('#collection_name').val();
    if(coll_name.trim()=="")
    {
      toast("Collection Name Can't Be Empty",{ transition: Zoom,});
      return false;
    }
    axios.post(CREATECOLLECTION,{
      'user_id':localStorage.getItem('user_id'),
      'tag':TAG,
      'collection_type':'image',
      'collection_name':coll_name
    },HEADER).then((res)=>{
      if(res.data.success==1)
      {
        var response=res.data;
          $(".modal .close").click()
        this.getData();
      //  toast(response.msg,{ transition: Zoom,});
      }
      else if(res.data.success==2){
        var response=res.data;
        toast(response.msg,{ transition: Zoom,});
      }
      else {
        var response=res.data;
        toast(response.msg,{ transition: Zoom,});
      }
    }).catch((error)=>{
      this.setState({not_found:true})
    })
  }
  render()
  {
    const {loading,loading_msg,collection_list}=this.state;
    if(loading)
    {
      const Image=(collection_list.length>0 && collection_list.map((res,key)=>{
        return(   <div class="cat_item"  key={key}>
              <div class="box">
                  <ul class="g_menu">
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-pencil" aria-hidden="true"></i>
                              Rename
                          </a>
                      </li>
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-share" aria-hidden="true"></i>
                              Share
                          </a>
                      </li>
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-files-o" aria-hidden="true"></i>
                              Copy
                          </a>
                      </li>
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-trash-o" aria-hidden="true"></i>
                              Delete
                          </a>
                      </li>
                  </ul>
                  <div class="img">
                        <Link to={`/web/imagecollection/${(res.collection_name.replace(/[&\/\\#, +()$~%.'":*?<>{}]/g,'')).toLowerCase()}/${res.id}`}>
                          <img src="images/dubbing1.jpg" alt="" />
                      </Link>
                  </div>
                  <div class="desc">
                    {res.collection_name}
                  </div>
                  <div class="price_info">
                      <a class="wish gig_ac" href="javascript:" data-hint="Collect action"><i class="fa fa-ellipsis-h" aria-hidden="true"></i></a>
                      <a class="count" href="javascript:">0</a>
                  </div>
              </div>
          </div> )
      }));
      return(<section class="collect_cont">
      <div class="modal fade" id="modalLoginForm" tabindex="-1" role="dialog" aria-labelledby="myModalLabel"
aria-hidden="true">
<div class="modal-dialog" role="document">
<div class="modal-content">
  <div class="modal-header text-center">
    <h4 class="modal-title w-100 font-weight-bold">Create Collection</h4>
    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
      <span aria-hidden="true">&times;</span>
    </button>
  </div>
  <div class="modal-body mx-3">
    <div class="md-form mb-5">
      <i class="fas fa-text"></i>
      <input type="text" id="collection_name" class="form-control validate"/>
      <label data-error="wrong" data-success="right" for="defaultForm-email">Your Collection Name</label>
    </div>
  </div>
  <span id="error_msg" style={{color:'red'}}></span>
  <div class="modal-footer d-flex justify-content-center">
    <button class="btn btn-default" onClick={()=>{this.createNewCollection()}}>Save</button>
  </div>
</div>
</div>
</div>
              <div class="container">
                  <div class="user_collect">
                      <h2>Collections <span>1</span></h2>
                      <div class="tab-content">
                          <div id="activegigs" class="tab-pane active">
                              <div class="cat_gal">
                                  <div class="cat_item">
                                      <div class="box">
                                      <a class="dropdown-item" href="javascript:;"  data-toggle="modal" data-target="#modalLoginForm" class=" add_gig "  disabled={this.state.cart_btn} >
                                              <span class="plus">+</span>
                                              <span>Create New</span>
                                          </a>
                                      </div>
                                  </div>

                                    {Image}
                              </div>
                              <div class="cat_gal creat_gig">

                              </div>
                          </div>
                      </div>
                  </div>
                  <div class="clearfix"></div>
              </div>
              <div class="clearfix"></div>
          </section>)
      }
      else {
        return(<div class="container loader_cont"><ActivityLoader
          type="Triangle"
           color="black"
           height={100}
           width={100}
           timeout={30000}/>
           <h3>{loading_msg}</h3>
           </div>)
      }
  }
}
export default Authentication(ImageCollection);
