              return(<div class="container loader_cont"><ActivityLoader
                type="Triangle"
                 color="black"
                 height={100}
                 width={100}
                 />
                 <h3>{loading_msg}</h3>
                 </div>);
            }
          }
    else {
      return(<Not_Found/>)
    }
