import React from 'react';
import {Link} from 'react-router-dom';
import {GETIMAGEDATA,USERID,TAG} from '../../url';
import axios from 'axios';
import Not_Found from '../../Component/not_found/not_found'
class ImageData extends React.Component{
  state={isLoading:true,image_id:'',image_name:'',not_found:false,image_data:[],similar_data:[]}
  componentDidMount()
  {
    this.setState({image_id:this.props.match.params.image_id,image_name:this.props.match.params.image_name},function()
  {
       setTimeout(()=>this.setState({isLoading:true}),1000);
    this.getData();
  });
  }
  getData=()=>{
    axios.post(GETIMAGEDATA,{
      'user_id':USERID,
      'tag':TAG,
      'image_id':this.state.image_id,
    }).then((res)=>{
      if(res.data.success==1)
      {
        var response=res.data;
        this.setState({image_data:response.data,similar_data:response.similar_data});
         setTimeout(()=>this.setState({isLoading:true}),1000);
      }
    }).catch((error)=>{
      this.setState({not_found:true})
    })
  }
  componentDidUpdate()
  {
    if(this.props.match.params.image_id!=this.state.image_id )
    {
      this.setState({image_id:this.props.match.params.image_id,image_name:this.props.match.params.image_name},function()
    {
         setTimeout(()=>this.setState({isLoading:true}),1000);
      this.getData();
    });
  }
}
imageSize=(bytes)=>{
   var sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
   if (bytes == 0) return '0 Byte';
   var i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)));
   return Math.round(bytes / Math.pow(1024, i), 2) + ' ' + sizes[i];
}
  render()
  {
  const {isLoading,not_found,image_data,similar_data}=this.state;
    if(!not_found)
    {
      const Similar_Data=(similar_data.length>0 && similar_data.map((res,key)=>{
        return( <div class="col col-md-3 col-sm-4" key={key}>
                <div class="box">
                    <Link to={`/web/image/${(res.title.replace(/[&\/\\#, +()$~%.'":*?<>{}]/g,'')).toLowerCase()}/${res.id}`}>
                        <img src={res.large_thumb} alt="" />
                        <div class="ov"></div>
                        <div class="q_t">
                            <span class="ql">4K</span>
                            <span class="tm">{res.dimension}</span>
                            <span class="ttle">{res.title}</span>
                        </div>
                    </Link>
                    <div class="sv_crt">
                        <button type="button" class="crt">
                            <i class="fa fa-cart-plus" aria-hidden="true"></i>
                            <span>Add to cart</span>
                        </button>
                        <button type="button" class="sv">
                            <i class="fa fa-plus-circle" aria-hidden="true"></i>
                            <span>Save</span>
                        </button>
                    </div>
                </div>
            </div>);
      }));
            if(isLoading)
            {
              return(
                <section class="inner_cont paymnt top_div">
                    <article class="payment_page">
                        <div class="container">
                            <div class="main_cont">
                                <div class="box">
                                    <div class="video_area">
                                          <img src={image_data.large_thumb}/>
                                        <div class="video_btns">
                                            <button class="icon">
                                                <i class="fa fa-plus-circle" aria-hidden="true"></i>
                                                <span>Save</span>
                                            </button>
                                            <button class="icon">
                                                <i class="fa fa-download" aria-hidden="true"></i>
                                                <span>Try</span>
                                            </button>
                                            <button class="icon">
                                                <i class="fa fa-upload" aria-hidden="true"></i>
                                                <span>Share</span>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <aside class="sidebar_view sticky-top">
                                <div class="payment_info">
                                    <h3><span>Stock Image</span>{image_data.short_desc}</h3>
                                    <div class="pay_user">
                                        <div class="img">
                                            <div class="icon">
                                                <img src="images/icon_payment_user.jpg" alt="" />
                                            </div>
                                            <div class="desc">
                                                <p class="title">By <a href="javascript:;">{image_data.artist_name}</a></p>
                                                <p>Stock footage ID: {image_data.request_id}</p>
                                            </div>
                                        </div>
                                        <div class="specf">
                                            <span><b>Video clip length:</b> 00:11</span>
                                            <span><b>FPS:</b> 25</span>
                                            <span><b>Aspect ratio:</b> 16:9</span>
                                        </div>
                                        <div class="modelno">
                                            <p>Singed model release: <a href="javascript:;">Standard image license</a></p>
                                        </div>
                                    </div>
                                    <div class="table-responsive">
                                        <table class="table table-hover">
                                            <tbody>
                                                <tr>
                                                    <td>
                                                        <label>{image_data.extension}
                                                            <input type="radio" name="radio" />
                                                            <span></span>
                                                        </label>
                                                    </td>
                                                    <td>
                                                        <span class="inr"><i class="fa fa-inr" aria-hidden="true"></i>{image_data.price}</span>
                                                    </td>
                                                    <td><span class="resl">{image_data.dimension}</span></td>
                                                    <td>{this.imageSize(image_data.size)}</td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                    <div class="buttons">
                                        <button type="button" class="btn btn_cart"><i class="fa fa-shopping-cart" aria-hidden="true"></i> Add to cart</button>
                                    </div>
                                </div>
                            </aside>
                        </div>
                    </article>
                    <article class="payment_page">
                        <div class="container">
                            {similar_data.length>0 && <div class="cat_i_v_list">
                                <h2>Visually similar stock image</h2>
                                <div class="row">
                                      {Similar_Data}
                                </div>
                                <div class="clearfix"></div>
                            </div>}
                        </div>
                    </article>
                    <div class="clearfix"></div>
                </section>)
            }
            else {
              return(<div>Loading..</div>);
            }
          }
    else {
      return(<Not_Found/>)
    }
  }
}
export default ImageData;
