import React from 'react';
import {LOGOUT,TAG,TOKEN,USERLIST,USERID,HEADER} from '../url.js';
import axios from 'axios';
import {Link} from 'react-router-dom'
class StickyHeader extends React.Component
{
  logout=()=>{
    axios.post(LOGOUT,{
      'tag':'dash'
    },HEADER).then((res)=>{
      if(res.data.success==1)
      {
        localStorage.clear();
      setTimeout(()=>window.location.href='/',1000);
      }
      else {
        alert(res.data.msg);
      }
    }).catch((error)=>{

    })
  }
  render()
  {
    const route_name=this.props.location.pathname.split('/')[2];
  return(
    <div class="sticky_header" style={{display:((route_name=='vendorservice_description' || route_name=='videocategorydata' || route_name=='imagecategorydata')?'none':'')}}>
        <div class="container">
            <div class="logo">
                <Link to="/"><img src="images/logo.png" alt="" /></Link>
            </div>
            <div class="searchbox">
                <form action="javascript:;">
                    <div class="inputbox">
                        <select class="select_i">
                          <option>Images</option>
                          <option>Videos</option>
                        </select>
                        <i class="fa fa-search" aria-hidden="true"></i>
                        <input type="text" placeholder="Search Photos and Videos" />
                        <button type="submit">Search</button>
                    </div>
                    <div class="clearfix"></div>
                </form>
                <div class="clearfix"></div>
            </div>
        </div>
    </div>)
        }
}
export default StickyHeader;
