import ReactDOM from "react-dom";
import React, { Component } from "react";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";

class Cropper extends Component {
  state = {
    src: null,
    croppedImageUrl:'',
    crop: {
      unit: 'px',
      width: 250,
      height: 375,
      x: 25,
      y: 25
    }
  };

  onSelectFile = e => {
    if (e.target.files && e.target.files.length > 0) {
      const reader = new FileReader();
      reader.addEventListener("load", () =>
        this.setState({ src: reader.result })
      );
      reader.readAsDataURL(e.target.files[0]);
    }
  };

  // If you setState the crop in here you should return false.
  onImageLoaded = image => {
    this.imageRef = image;
  };

  onCropComplete = crop => {
    this.makeClientCrop(crop);
  };

  onCropChange = (crop, percentCrop) => {
    // You could also use percentCrop:
    // this.setState({ crop: percentCrop });
    this.setState({ crop });
  };

  async makeClientCrop(crop) {
    if (this.imageRef && crop.width && crop.height) {
      const croppedImageUrl = await this.getCroppedImg(
        this.imageRef,
        crop,
        "newFile.jpeg"
      );
      this.setState({ croppedImageUrl });
    }
  }

  getCroppedImg(image, crop, fileName) {
    const canvas = document.createElement("canvas");
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    canvas.width = crop.width;
    canvas.height = crop.height;
    const ctx = canvas.getContext("2d");

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return new Promise((resolve, reject) => {
      canvas.toBlob(blob => {
        if (!blob) {
          //reject(new Error('Canvas is empty'));
          console.error("Canvas is empty");
          return;
        }
        blob.name = fileName;
        window.URL.revokeObjectURL(this.fileUrl);
        this.fileUrl = window.URL.createObjectURL(blob);
        resolve(this.fileUrl);
      }, "image/jpeg");
    });
  }

  render() {
    const { crop, croppedImageUrl, src } = this.state;
    return (
      <div className="App">
        <div>
          {croppedImageUrl=='' && <label for="upload_vimg1" class="crop_upload">
              <input type="file" id="upload_vimg1" accept="image/*" onChange={this.onSelectFile} />
              <i class="fa fa-upload" aria-hidden="true"></i>
              <b>Upload key artwork <sup>*</sup></b>
              <p>Click here to choose poster file to upload</p>
              <img src="" id="bid_thumbnail" alt="" />
          </label>}
        </div>
        <div class="crop_container">
          {src && (
            <ReactCrop
              src={src}
              crop={crop}
              style={{height:200}}
              maxHeight={375}
              minHeight={375}
              maxWidth={250}
              minWidth={250}
              ruleOfThirds
              onImageLoaded={this.onImageLoaded}
              onComplete={this.onCropComplete}
              onChange={this.onCropChange}
            />
          )}
        </div>
        {croppedImageUrl && (
          <div class="cropped_img"><img alt="Crop" style={{ maxWidth: "50%" }} src={croppedImageUrl} /></div>
        )}
      </div>
    );
  }
}

export default Cropper;
