import React from 'react';
import Authentication from '../Authentication/Authentication';
import {GETUSERORDERS,USERID,TAG,TOKEN} from '../../url';
import axios from 'axios';
import $ from 'jquery'
import {Link} from 'react-router-dom'
import { ToastContainer, toast,cssTransition} from 'react-toastify';
import ActivityLoader from 'react-loader-spinner';
const Zoom = cssTransition({
enter: 'zoomIn',
exit: 'zoomOut',
duration: 750,
});
const HEADER = {
headers: {
  'Content-Type': 'application/json;charset=UTF-8',
  'Authorization':"Bearer " + localStorage.getItem('planetshare_web_token'),
}
};
class Orders extends React.Component
{
  state={not_found:false,loading:false,loading_msg:'Please Wait',order_list:[]}
  componentDidMount()
  {
    this.getData();
  }
  getData=()=>{
    axios.post(GETUSERORDERS,{user_id:USERID,tag:TAG},HEADER).then((res)=>{
      if(res.data.success==1)
      {
        var response=res.data;
        this.setState({order_list:response.data})
          setTimeout(()=>this.setState({loading:true}),1000)
      }
    }).catch((error)=>{

    })
  }
  render()
  {
    const {loading,loading_msg,order_list}=this.state;
    if(loading)
    {
      const Orders=(order_list.length>0 && order_list.map((res,key)=>{
        return(   <div class="cat_item"  key={key}>
              <div class="box">
                  <ul class="g_menu">
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-pencil" aria-hidden="true"></i>
                              Rename
                          </a>
                      </li>
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-share" aria-hidden="true"></i>
                              Share
                          </a>
                      </li>
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-files-o" aria-hidden="true"></i>
                              Copy
                          </a>
                      </li>
                      <li>
                          <a href="javascript:">
                              <i class="fa fa-trash-o" aria-hidden="true"></i>
                              Delete
                          </a>
                      </li>
                  </ul>
                  <div class="img">

                          <img src="images/dubbing1.jpg" alt="" />

                  </div>
                  <div class="desc">
                    {res.order_info.pack_name}
                  </div>
                  <div class="price_info">
                      <a class="wish gig_ac" href="javascript:" data-hint="Collect action"><i class="fa fa-ellipsis-h" aria-hidden="true"></i></a>
                      <a class="count" href="javascript:">{res.order_info.pack_price}</a>
                  </div>
              </div>
          </div> )
      }));
      return(<section class="collect_cont">
              <div class="container">
                  <div class="user_collect">
                      <h2>Orders <span>1</span></h2>
                      <div class="tab-content">
                          <div id="activegigs" class="tab-pane active">
                              <div class="cat_gal">


                                    {Orders}
                              </div>
                              <div class="cat_gal creat_gig">

                              </div>
                          </div>
                      </div>
                  </div>
                  <div class="clearfix"></div>
              </div>
              <div class="clearfix"></div>
          </section>)
      }
      else {
        return(<div class="container loader_cont"><ActivityLoader
          type="Triangle"
           color="black"
           height={100}
           width={100}
           timeout={30000}/>
           <h3>{loading_msg}</h3>
           </div>)
      }
  }
}
export default Authentication(Orders);
