import React, { Component } from 'react';
import './login.css';
import $ from 'jquery';
import axios from 'axios';
import {LOGIN,TAG,REGISTER} from '../../url.js';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import ActivityLoader from 'react-loader-spinner';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast,cssTransition} from 'react-toastify';
const Zoom = cssTransition({
enter: 'zoomIn',
exit: 'zoomOut',
duration: 750,
});
class Login extends Component {
  state={isLoading:false,sigin:true,signup:false,loading:false,loading_msg:''}
loginFormSubmit=(e)=>
{
    e.preventDefault();
    $('#login_err_msg').html('');
      var emailid=e.target.lemail.value.trim();
      var password=e.target.lpassword.value.trim();
      axios.post(LOGIN, {
      email:emailid,
      password:password,
      tag:TAG
    })
    .then(response=>{
        if(response.data.success=='1'){
          localStorage.setItem('status','Y');
          localStorage.setItem('name',response.data.name);
          localStorage.setItem('planetshare_web_token',response.data.token);
          localStorage.setItem('seller_id',response.data.seller_id);
          localStorage.setItem('vendor_id',response.data.vendor_id);
          localStorage.setItem('email',response.data.email);
          localStorage.setItem('seller_id',response.data.seller_id);
          localStorage.setItem('user_id',response.data.user_id);

          //setTimeout(()=>this.props.history.push('/'),1500);
            this.setState({loading:true,loading_msg:'Logging In Please Wait'});
          setTimeout(()=>window.location.href='/',1000);
      //    setTimeout(()=>{this.props.history.push('/web/vendor');this.props.history.replace('/web')},1500);

        }
        else {
          $('#login_err_msg').html('Invalid Credentials');
        }
    })
    .catch(function (error) {
      console.log(error);
    });
}
normalLogin=(emailid,password)=>
{
    $('#login_err_msg').html('');
      axios.post(LOGIN, {
      email:emailid,
      password:password,
      tag:TAG
    })
    .then(response=>{
        if(response.data.success=='1'){
          localStorage.setItem('status','Y');
          localStorage.setItem('name',response.data.name);
          localStorage.setItem('planetshare_web_token',response.data.token);
          localStorage.setItem('email',response.data.email);
          localStorage.setItem('seller_id',response.data.seller_id);
          localStorage.setItem('user_id',response.data.user_id);

          //setTimeout(()=>this.props.history.push('/'),1500);
          this.setState({loading:true,loading_msg:'Logging In Please Wait'});
          setTimeout(()=>window.location.href='/',1000);

        }
        else {
          $('#login_err_msg').html('Invalid Credentials');
        }
    })
    .catch(function (error) {
      console.log(error);
    });
}
registerFormSubmit=(e)=>
{
    e.preventDefault();
    $('#register_err_msg').html('');
      var name=e.target.pname.value.trim();
      var emailid=e.target.pemail.value.trim();
      var password=e.target.ppass.value.trim();
      var cpassword=e.target.pcpass.value.trim();
      if(name=='' || emailid=='' || password=='' || cpassword=='')
      {
        toast('All Fields Must Be Filled',{ transition: Zoom,});
        return false;
      }
      if(password!=cpassword)
      {
        toast('Password should be same',{ transition: Zoom,});
        return false;
      }
      axios.post(REGISTER, {
      name:name,
      email:emailid,
      password:password,
      tag:TAG
    })
    .then(response=>{
        if(response.data.success=='1'){
          //setTimeout(()=>this.props.history.push('/'),1500);
          this.setState({loading:true,loading_msg:`Registering ${name} Please Wait`});
          setTimeout(()=>this.normalLogin(emailid,password),1000);
          //setTimeout(()=>window.location.href='/',1000);

        }
        else {
          toast('Something Went Wrong',{ transition: Zoom,});
          $('#register_err_msg').html('Invalid Credentials');
        }
    })
    .catch(function (error) {
      console.log(error);
    });
}
  render() {
    const {signup,sigin,isLoading,loading,loading_msg}=this.state;
    if(!isLoading)
    {
      if(!loading)
      {
    return (
      <section class="login_page">
          <ToastContainer autoClose={1000}/>
          {sigin && <article class="login">
              <form onSubmit={this.loginFormSubmit} id="login_form" >
                  <div class="logo">
                      <a href="index.html">
                          <img src="images/logo.png" alt="" />
                      </a>
                  </div>
                  <h2>Log In</h2>
                  <div class="fields">
                      <div class="inputbox">
                          <label>Email</label>
                          <input type="email" placeholder="email" name="lemail" id="lemail" required/>
                      </div>
                      <div class="inputbox">
                          <label>Password</label>
                          <input type="password" placeholder="password" name="lpassword" id="lpassword" required/>
                      </div>
                  </div>
                      <span id="login_err_msg"></span>
                  <a href="javascript:;" class="register" onClick={()=>this.setState({sigin:false,signup:true})}>Create An Account</a>
                  <div class="buttons">
                      <button type="submit" class="btn btn_submit">Login</button>
                  </div>
              </form>
          </article>}

          {signup && <article class="login" >
              <form onSubmit={this.registerFormSubmit}>
                  <div class="logo">
                      <a href="index.html">
                          <img src="images/logo.png" alt="" />
                      </a>
                  </div>
                  {/*<h2>Register</h2>*/}
                  <div class="fields">
                      <div class="inputbox">
                          <label>Name</label>
                          <input type="text" placeholder="Full Name" name="pname" required/>
                      </div>
                      <div class="inputbox">
                          <label>Email</label>
                          <input type="email" placeholder="email" name="pemail" required/>
                      </div>
                      <div class="inputbox">
                          <label>Password</label>
                          <input type="password" placeholder="password" name="ppass" required/>
                      </div>
                      <div class="inputbox">
                          <label>Confirm Password</label>
                          <input type="password" placeholder="confirm password" name="pcpass" required/>
                      </div>
                  </div>
                    <span id="register_err_msg"></span>
                  <a href="javascript:;" class="register" onClick={()=>this.setState({sigin:true,signup:false})}>Already a member</a>
                  <div class="buttons">
                      <button type="submit" class="btn btn_submit">Register</button>
                  </div>
              </form>
          </article>}
          <div class="clearfix"></div>
      </section>
)
}
else {
  return(<div class="container"><ActivityLoader
    type="Triangle"
     color="black"
     height={100}
     width={100}
     timeout={30000}/>
     <h3>{loading_msg}</h3>
     </div>)
}
}
else {
  return(<div>Loading..</div>)
}
}
}
export default Login;
