import React from 'react';
import {Link} from 'react-router-dom';
import {GETVIDEODATA,USERID,TAG} from '../../url';
import axios from 'axios';
import ReactJWPlayer from 'react-jw-player';
import Not_Found from '../../Component/not_found/not_found'
class ImageData extends React.Component{
  state={isLoading:true,video_id:'',video_name:'',not_found:false,video_data:[],similar_data:[]}
  componentDidMount()
  {
    this.setState({video_id:this.props.match.params.video_id,video_name:this.props.match.params.video_name},function()
  {
       setTimeout(()=>this.setState({isLoading:true}),1000);
    this.getData();
  });
  }
  getData=()=>{
    axios.post(GETVIDEODATA,{
      'user_id':USERID,
      'tag':TAG,
      'video_id':this.state.video_id,
    }).then((res)=>{
      if(res.data.success==1)
      {
        var response=res.data;
        this.setState({video_data:response.data,similar_data:response.similar_data});
         setTimeout(()=>this.setState({isLoading:true}),1000);
      }
    }).catch((error)=>{
      this.setState({not_found:true})
    })
  }
  componentDidUpdate()
  {
    if(this.props.match.params.image_id!=this.state.image_id )
    {
      this.setState({video_id:this.props.match.params.video_id,video_name:this.props.match.params.video_name},function()
    {
         setTimeout(()=>this.setState({isLoading:true}),1000);
      this.getData();
    });
  }
}
videoSize=(bytes)=>{
   var sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
   if (bytes == 0) return '0 Byte';
   var i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)));
   return Math.round(bytes / Math.pow(1024, i), 2) + ' ' + sizes[i];
}
  render()
  {
  const {isLoading,not_found,video_data,similar_data}=this.state;
  const Similar_Data=(similar_data.length>0 && similar_data.map((res,key)=>{
    return( <div class="col col-md-3 col-sm-4" key={key}>
            <div class="box">
                <Link to={`/web/image/${(res.title.replace(/[&\/\\#, +()$~%.'":*?<>{}]/g,'')).toLowerCase()}/${res.id}`}>
                    <img src={res.large_thumb} alt="" />
                    <div class="ov"></div>
                    <div class="q_t">
                        <span class="ql">4K</span>
                        <span class="tm">{res.dimension}</span>
                        <span class="ttle">{res.title}</span>
                    </div>
                </Link>
                <div class="sv_crt">
                    <button type="button" class="crt">
                        <i class="fa fa-cart-plus" aria-hidden="true"></i>
                        <span>Add to cart</span>
                    </button>
                    <button type="button" class="sv">
                        <i class="fa fa-plus-circle" aria-hidden="true"></i>
                        <span>Save</span>
                    </button>
                </div>
            </div>
        </div>);
  }));
    if(!not_found)
    {
            if(isLoading)
            {
              return(
                <section class="inner_cont paymnt top_div">
                    <article class="payment_page">
                        <div class="container">
                            <div class="main_cont">
                                <div class="box">
                                    <div class="video_area">
                                    <ReactJWPlayer
                                    style={{backgroundColor:'grey'}}
                                    ref={(ref) => { this.player = ref }}
                                    playerId='jw-player'
                                    image={`${video_data.large_thumb}`}
                                    playerScript='https://content.jwplatform.com/libraries/jvJ1Gu3c.js'
                                    file={video_data.demo_url}
                                    aspectRatio="16:9"
                                    className="col-md-12"
                                    isAutoPlay={false}
                                    controls={true}
                                    repeat="true"
                                      customProps={{
                                        controls: true,
                                        repeat: true,
                                        stretching: 'fill',
                                        preload: 'auto',
                                        volume: 100,
                                        width: '100%',
                                        height: '100%',
                                    }}
                                    />
                                        <div class="video_btns">
                                            <button class="icon">
                                                <i class="fa fa-plus-circle" aria-hidden="true"></i>
                                                <span>Save</span>
                                            </button>
                                            <button class="icon">
                                                <i class="fa fa-download" aria-hidden="true"></i>
                                                <span>Try</span>
                                            </button>
                                            <button class="icon">
                                                <i class="fa fa-upload" aria-hidden="true"></i>
                                                <span>Share</span>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <aside class="sidebar_view sticky-top">
                                <div class="payment_info">
                                    <h3><span>Stock Footage</span>{video_data.short_desc}</h3>
                                    <div class="pay_user">
                                        <div class="img">
                                            <div class="icon">
                                                <img src="images/icon_payment_user.jpg" alt="" />
                                            </div>
                                            <div class="desc">
                                                <p class="title">By <a href="javascript:;">{video_data.artist_name}</a></p>
                                                <p>Stock footage ID: {video_data.request_id}</p>
                                            </div>
                                        </div>
                                        <div class="specf">
                                            <span><b>Video clip length:</b> 00:11</span>
                                            <span><b>FPS:</b> 25</span>
                                            <span><b>Aspect ratio:</b> 16:9</span>
                                        </div>
                                        <div class="modelno">
                                            <p>Singed model release: <a href="javascript:;">Standard footage license</a></p>
                                        </div>
                                    </div>
                                    <div class="table-responsive">
                                        <table class="table table-hover">
                                            <tbody>
                                                <tr>
                                                    <td>
                                                        <label>{video_data.extension}
                                                            <input type="radio" name="radio" />
                                                            <span></span>
                                                        </label>
                                                    </td>
                                                    <td>
                                                        <span class="inr">INR<i class="fa fa-inr" aria-hidden="true"></i> 179</span>
                                                    </td>
                                                      <td>{this.videoSize(572656516)}</td>
                                                </tr>
                                            </tbody>
                                        </table>
                                    </div>
                                    <div class="buttons">
                                        <button type="button" class="btn btn_cart"><i class="fa fa-shopping-cart" aria-hidden="true"></i> Add to cart</button>
                                    </div>
                                </div>
                            </aside>
                        </div>
                    </article>
                    <article class="payment_page">
                        <div class="container">
                            <div class="cat_i_v_list">
                              {similar_data.length>0 &&   <h2>Visually similar stock footage</h2>}
                                <div class="row">
                                  {Similar_Data}
                                </div>
                                <div class="clearfix"></div>
                            </div>
                        </div>
                    </article>
                    <div class="clearfix"></div>
                </section>)
            }
            else {
              return(<div>Loading..</div>);
            }
          }
    else {
      return(<Not_Found/>)
    }
  }
}
export default ImageData;
