import React from 'react';
import StepZilla from "react-stepzilla";
import './steps.css';
import {GETLANGUAGELIST} from '../../../../url';
import axios from 'axios'
import CreateListing from './CreateListing'
import MediaUploads from './MediaUploads'
import SetRights from './SetRights'
class Bidding extends React.Component
{
  state={languageList:[]}
  componentDidMount()
  {
    axios.get(GETLANGUAGELIST).then((res)=>{
      if(res.data.success==1)
      {
         this.setState({languageList:res.data.data})
      }
    }).catch((err)=>{

    })
  }
  render()
  {
    const steps =[
          {name: 'Create List', component: <CreateListing languageList={this.state.languageList}/>},
          {name: 'Media Uploads', component: <MediaUploads languageList={this.state.languageList}/>},
          {name: 'Rights', component: <SetRights languageList={this.state.languageList}/>},
        ]
    return(
      <section class="dashb_cont">
        <div class="container">
            <div class="content_dash bidding_dash">
            <CreateListing languageList={this.state.languageList}/>
            </div>
            <div class="clearfix"></div>
        </div>
        <div class="clearfix"></div>
    </section>)
  }
}
export default Bidding;
