                            <div class="clearfix"></div>
                        </div>}
                        <div class="desc makeoffr" id="make_offer">
                            <h3>Offer Price</h3>
                            <div class="lang">
                                <p><i class={bid_data.box_office_price.currency=="USD"?"fa fa-usd":"fa fa-inr"} aria-hidden="true"></i>{bid_data.box_office_price.price}</p>
                            </div>
                            <h3 class="ofr_btn">
                              {offer_status_data==null &&<button class="s_btn" onClick={()=>{
                                  this.props.history.push(`/web/bidding/perf_bid/${bid_data.request_id}/${this.state.bid_id}`)
                                }}>Make an offer <i class="fa fa-plus"></i></button>}
