import React,{} from 'react';
import axios from 'axios';
import  {Redirect} from 'react-router-dom';
import {BUYERPROFILE,USERID,TAG} from '../../url'
import LoadingGif from '../../Component/Loader/loading_gif'
import Authentication from '../Authentication/Authentication';
import $ from 'jquery';
import Portal from './ProfilePortal';
import { ToastContainer, toast,cssTransition} from 'react-toastify';
const Zoom = cssTransition({
enter: 'zoomIn',
exit: 'zoomOut',
duration: 750,
});
const HEADER = {
headers: {
  'Content-Type': 'application/json;charset=UTF-8',
  'Authorization':"Bearer " + localStorage.getItem('planetshare_web_token'),
}
};
class Profile extends React.Component
{
  state={modal_type:'',not_found:false,loading:false,loading_msg:'Please Wait',user_info:{},email:'',name:'',auth_err:'',profile_portal:false,profile_loader:false}
  componentDidMount()
  {
    this.getData();
  }
  getData=()=>{
    axios.post(BUYERPROFILE,{user_id:localStorage.getItem('user_id'),tag:TAG},HEADER).then((res)=>{
      if(res.data.success==1)
      {
        var response=res.data;
        this.setState({user_info:response.data,name:response.data.name,email:response.data.email})
        setTimeout(()=>this.setState({loading:true}),1000)
      }
      else
      {

      }
    }).catch((error)=>{
      this.props.history.push('/web')
    })
  }
  openPortal=(tag)=>{
    if(tag=="name")
    {
      this.setState({profile_portal:true,modal_type:'name'});
      $("#portal_modal").addClass("show")
    }
    else if(tag=="email")
    {
      this.setState({profile_portal:true,modal_type:'email'});
      $("#portal_modal").addClass("show")
    }
    else {
      this.setState({profile_portal:true,modal_type:'password'});
      $("#portal_modal").addClass("show")
    }
  }

  render()
  {
    useEffect(()=>{
      var lastId,
       topMenu = $(".edit_prof .side_links .p_links"),
       topMenuHeight = topMenu.outerHeight()+150,
       // All list items
       menuItems = topMenu.find("a"),
       // Anchors corresponding to menu items
       scrollItems = menuItems.map(function(){
           var currentLink = $(this);
         var item = $(currentLink.attr('href'));
         if (item.length) { return item; }
       });

    // Bind click handler to menu items
    // so we can get a fancy scroll animation
    menuItems.click(function(e){
     var href = $(this).attr("href")
      var offsetTop = href == "#" ? 0 : $(href).offset().top-80;
     $('html, body').stop().animate({
         scrollTop: offsetTop
     }, 300);
     e.preventDefault();
    });

    // Bind to scroll
    $(window).scroll(function(){
      // Get container scroll position
      var fromTop = $(this).scrollTop()+topMenuHeight;

      // Get id of current scroll item
      var cur = scrollItems.map(function(){
        if ($(this).offset().top < fromTop)
          return this;
      });
      // Get the id of the current element
      cur = cur[cur.length-1];
      var id = cur && cur.length ? cur[0].id : "";

      if (lastId !== id) {
          lastId = id;
          // Set/remove active class
          menuItems.parent().removeClass("active").end().filter("[href='#"+id+"']").parent().addClass("active");
      }
    });
    })
    const {loading,loading_msg,user_info}=this.state;
    if(loading)
    {
    return( <section class="edit_user_prof">
