import React from 'react';
import {Redirect} from 'react-router-dom';
import VendorRegistration from "./vendor_registration.js"
import {USERID,VENDORID} from '../../../url.js'
const VendorAuth=(OldComponent)=>{
  class NewComponent extends React.Component{
    render()
    {
      if(USERID!=0 && localStorage.getItem('vendor_id')!=0)
      {
      return(<OldComponent {...this.props}/>)
      }
      else if(USERID!=0 && localStorage.getItem('vendor_id')==0)
      {
      return(<VendorRegistration {...this.props}/>)
      }
      else {
        return(<Redirect to="/login"/>)
      }
    }
  }
  return NewComponent;
}

export default VendorAuth;
