import React from 'react';
import {Redirect} from 'react-router-dom';
import SellerrRegistration from "./seller_registration.js"
import {USERID,SELLERID} from '../../../url.js'
const VendorAuth=(OldComponent)=>{
  class NewComponent extends React.Component{
    render()
    {
      if(localStorage.getItem('user_id')!=0 && localStorage.getItem('seller_id')!=0)
      {
      return(<OldComponent {...this.props}/>)
      }
      else if(localStorage.getItem('user_id')!=0 && localStorage.getItem('seller_id')==0)
      {
      return(<SellerrRegistration {...this.props}/>)
      }
      else {
        return(<Redirect to="/login"/>)
      }
    }
  }
  return NewComponent;
}

export default VendorAuth;
