import React, { Component } from 'react';
import Authentication from '../Authentication/Authentication';
import {Link,Redirect} from "react-router-dom";
import {GETPACKS,TAG,TOKEN,USERID,HEADER,CHANGE_STATUS} from '../../url.js';
import BreadCrumb from '../../Component/breadcrumb';
import $ from 'jquery';
import axios from 'axios';
import Loader from '../../Component/Loader/loader';
import  Modal from 'boron-react-modal/ScaleModal';
class Packs extends Component {
  state={
    isLoading:false,packList:[],
    currentPage:1,
    total:0,pack_type:'image',vidSelect:false,imgSelect:true
}
  componentDidMount()
  {
    this.getData()
  }
  getData=()=>{
    axios.post(GETPACKS,{
      'tag':TAG,
      'pack_type':this.state.pack_type,
      'user_id':USERID
    }).then((res)=>{
        if(res.data.success==1)
        {
          var response=res.data;
          this.setState({packList:response.data});
          setTimeout(()=>this.setState({isLoading:true}),1000)
        }
    }).catch((error)=>{

    })
  }
  status=(Id,status)=>{
    var name="";
    if(status==1)
    {
      name="Active";
    }
    if(status==0)
    {
      name="Inactive";
    }
      return( <div class="status_btn">
              <button type="button" className={`btn ${status==1?'btn-success':'btn-danger'} btn-sm`} id={`status_${Id}`}>{name}</button>
            </div>);
  }
  changeStatus=(Id,status)=>{
    axios.post(CHANGE_STATUS,{
      'tag':'dash',
      'user_id':USERID,
      'role':'userlist',
      'id':Id,
      'status':!status
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
          var list=this.state.userList;
            for(var i=0;i<list.length;i++)
            {
              if(list[i].id==Id)
              {
                list[i].status=!status;
              }
            }
            this.setState({userList:list});
        }
    }).catch((error)=>{

    })
  }
  showModal=()=>{
       this.refs.modal.show();
   }
   hideModal=()=>{
     this.setState({err_result:[]})
       this.refs.modal.hide();
   }
  packType=(type)=>{
    if(type=="image")
    {
      this.setState({pack_type:'image',imgSelect:true,vidSelect:false},function()
    {
      this.getData();
    })
    }
    else {
      this.setState({pack_type:'video',imgSelect:false,vidSelect:true},function()
    {
      this.getData();
    })
    }
  }
  render() {
    const {packList,isLoading}=this.state;
    const tableContent=packList.map((res,key)=>{
      return(<tr key={key}><td>{res.request_id}</td>
                <td>{res.pack_name}</td>
                <td>{res.pack_count}</td>
                <td>{res.pack_price}</td>
                <td>{res.pack_expire_time}</td>
                <td>{res.content_type}</td>
                <td>{this.status(res.id,res.status)}</td>
                <td>
                  <div class="action_buttons">
                    <a href="javascript:" class="btn"><i class="fa fa-pencil"></i></a>
                    <a class="btn" href="javascript:"><i class="fa fa-trash-o" aria-hidden="true"></i></a>
                    <div class="dropdown">
                      <a href="javascript:;" class="btn dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                          <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                      </a>
                      <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                          <a class="dropdown-item" href="javascript;;"><i class="fa fa-trash-o" aria-hidden="true"></i> Delete</a>
                      </div>
                    </div>
                  </div>
                </td>
                </tr>)
    });
    if(isLoading)
    {
    return (
      <div id="content">
        <div class="content_area">
          <BreadCrumb name="PackList"/>
          <div class="adds_area">
            <button type="button" className="s_btn" onClick={()=>this.props.history.push(`/packs/create`)}>Add packs<i class="fa fa-plus"></i></button>
          </div>
          <div class="adds_select">
            <h3>Packs</h3>
            <select onChange={(e)=>this.packType(e.target.value)}><option value="image" selected={this.state.imgSelect}>Image</option><option value="video" selected={this.state.vidSelect}>Video</option></select>
          </div>


          <Modal className="m_show" ref="modal"  backdropStyle={backdropStyle} contentStyle={contentStyle}>
              <div class="modal-dialog" role="document">
                <div class="modal-content">
                  <div class="modal-header text-center">
                    <h4 class="modal-title">Create Category</h4>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close" onClick={this.hideModal}>
                      <span aria-hidden="true">&times;</span>
                    </button>
                  </div>
                  <form class="form_control" onSubmit={this.formSubmit}>
                    <div class="field_area">
                      <div class="fields">
                        <div class="label">
                            <label>
                                <span>Category Name</span>
                            </label>
                        </div>
                        <div class="inputbox">
                          <input type="text" name="cat_name" required/>
                        </div>
                      </div>
                      <div class="fields">
                        <div class="label">
                            <label>
                                <span>Icon</span>
                            </label>
                        </div>
                        <div class="inputbox">
                          <input type="text" name="cat_icon" required/>
                        </div>
                      </div>
                      <div class="fields">
                        <div class="label">
                            <label>
                                <span>Description</span>
                            </label>
                        </div>
                        <div class="inputbox">
                          <textarea  name="cat_desc" required></textarea>
                        </div>
                      </div>
                      <div class="fields">
                        <div class="label">
                            <label>
                                <span>Category Type</span>
                            </label>
                        </div>
                        <div class="inputbox">
                          <select name="pack_type" required onChange={(e)=>{this.setState({cat_type:e.target.value})}}>
                            <option value="image">Image</option>
                            <option value="video">Video</option>
                          </select>
                        </div>
                      </div>
                    </div>
                    <div class="build_gal">
                        <div class="upload_area">
                            <label>Thumbnail <span>Upload thumbnail that describe or are related to your category.</span></label>
                            <ul class="browse_list">
                                <li>
                                    <div class="upl_box">
                                        <div class="drag_area">
                                            <span>Drag a Photo or</span>
                                            <label for="gig_upload">
                                            <input
                                                ref="file"
                                                type="file"
                                                name="user[image]"
                                                accept="image/*"
                                                id="gig_upload"
                                                required
                                                onChange={this._onChange}/>
                                                <input type="file"  />
                                                <span>Browse</span>
                                                {this.state.imgs && [...this.state.imgs].map((file)=>(
                                                      <img src="" id="upload_img"  src={URL.createObjectURL(file)} alt="" />   ))}

                                            </label>
                                        </div>
                                    </div>
                                </li>
                            </ul>
                        </div>
                        </div>
                      <div class="modal-footer">
                      {err_result.length >0 && <div class="error_box">
                              <ul>
                                {err_result.map((res,key)=>{
                                  return(<li key={key}><p>{res}</p></li>)
                                })}
                              </ul>
                            </div>}
                        <button type="submit"  class="btn btndefault" disabled={this.state.btn_disabled}>Save</button>
                      </div>

                  </form>
                  <span id="error_msg" style={{color:'red'}}></span>

                </div>
              </div>
          </Modal>


          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th>Pack Id</th>
                  <th>Pack Name</th>
                  <th>Pack Count</th>
                  <th>Pack Price</th>
                  <th>Expire Time(in days)</th>
                  <th>Content</th>
                  <th>Status</th>
                  <th class="ac">Action</th>
                </tr>
              </thead>
              <tbody>
              {tableContent}

              </tbody>
            </table>
          </div>
        </div>


      </div>
    );
   }
  else {
    return(<div id="content"><Loader/></div>);
  }
}
}

export default Authentication(Packs);
