import React, { Component } from 'react';
import Authentication from '../Authentication/Authentication';
import {Link,Redirect} from "react-router-dom";
import {LOGIN,TAG,TOKEN,SELLERLIST,USERID,HEADER,CHANGE_STATUS} from '../../url.js';
import BreadCrumb from '../../Component/breadcrumb';
import Loader from '../../Component/Loader/loader';
import Pagination from "react-js-pagination";
import axios from 'axios';
import $ from 'jquery';
import  Modal from 'boron-react-modal/OutlineModal';
import cogoToast from 'cogo-toast';
const modalStyle = {
    // transform: 'rotate(45deg) translateX(-50%)',
};

const backdropStyle = {
    backgroundColor: 'black'
};

const contentStyle = {
    backgroundColor: 'white',
    height: '100%'
};

class SellerList extends Component {
  state={
    isLoading:false,sellerList:[],
    currentPage:1,
       total:1,itemsCountPerPage:10,pageRangeDisplayed:3
}
  componentDidMount()
  {
    cogoToast.loading('Loading your data...').then(() => {
    this.getData(1,10)
  cogoToast.success('Data Successfully Loaded');
});

  }
  getData=(page)=>{
    axios.post(`${SELLERLIST}?page=${page}`,{
      'tag':'dash',
      'limit':this.state.itemsCountPerPage,
      'user_id':USERID
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
          var response=res.data.data;
          this.setState({currentPage:response.current_page,total:response.total,sellerList:response.data});
          setTimeout(()=>this.setState({isLoading:true}),1000)
        }
    }).catch((error)=>{

    })
  }
  status=(Id,status)=>{
    var name="";
    if(status==1)
    {
      name="Active";
    }
    if(status==0)
    {
      name="Inactive";
    }
      return( <div class="status_btn">
              <button type="button" className={`btn ${status==1?'btn-success':'btn-danger'} btn-sm`} id={`status_${Id}`} onClick={()=>this.changeStatus(Id,status)}>{name}</button>
            </div>);
  }
  changeStatus=(Id,status)=>{
      cogoToast.loading('Updating Status').then(() => {axios.post(CHANGE_STATUS,{
      'tag':'dash',
      'user_id':USERID,
      'role':'sellerlist',
      'id':Id,
      'status':!status
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
          var list=this.state.sellerList;
            for(var i=0;i<list.length;i++)
            {
              if(list[i].id==Id)
              {
                list[i].status=!status;
              }
            }
            this.setState({sellerList:list})
            //this.getData(1,10)
          // if(status==1)
          // {
          //   $('#status_'+Id).removeClass( "btn-success" ).addClass( "btn-danger" )
          //   $('#status_'+Id).html('Inactive');
          // }
          // if(status==0)
          // {
          //   $('#status_'+Id).removeClass( "btn-danger" ).addClass( "btn-success" );
          //   $('#status_'+Id).html('Active');
          // }


        }
    }).catch((error)=>{

    })
    cogoToast.success('Status Changed Successfully');
  });
  }
  showModal=()=>{
       this.refs.modal.show();
   }
   hideModal=()=>{
       this.refs.modal.hide();
   }
  render() {
    const {sellerList,isLoading}=this.state;
    const tableContent=sellerList.map((res,key)=>{
      return(<tr key={key}><td>{res.Accountid}</td>
                <td>{res.display_name}</td>
                <td>{res.phone_number}</td>
                <td>{res.created_at}</td>
                <td>Application</td>
                <td>{this.status(res.id,res.status)}</td>
                <td>
                <div class="dropdown">
                  <button class="btn btn-outline-secondary btn-sm dropdown-toggle" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                      Active
                  </button>
                  <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                      <a class="dropdown-item" href="javascript:;"><i class="fa fa-eye" aria-hidden="true"></i> View Entries</a>
                      <a class="dropdown-item" href="javascript:;"><i class="fa fa-pencil-square-o" aria-hidden="true"></i> Edit</a>
                      <a class="dropdown-item" href="javascript;;"><i class="fa fa-trash-o" aria-hidden="true"></i> Delete</a>
                  </div>
              </div>
                </td></tr>)
    })
    if(isLoading)
    {
    return (
      <div id="content">
        <div class="content_area">
          <BreadCrumb name="SellerList"/>
          <button onClick={this.showModal}><i class="fa fa-plus"></i></button>
          <Modal ref="modal" modalStyle={modalStyle} backdropStyle={backdropStyle} contentStyle={contentStyle}>

            <div class="modal-dialog" role="document">
              <div class="modal-content">
                <div class="modal-header text-center">
                  <h4 class="modal-title">Create Collection</h4>
                  <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <div class="modal-body">
                  <div class="md-form">
                    <i class="fas fa-text"></i>
                    <label data-error="wrong" data-success="right" for="defaultForm-email">Your Collection Name</label>
                    <input type="text" id="collection_name" class="form-control validate"/>
                  </div>
                </div>
                <span id="error_msg" style={{color:'red'}}></span>
                <div class="modal-footer">
                  <button class="btn btndefault" >Save</button>
                </div>
              </div>
            </div>

                    <button onClick={this.hideModal}>Close</button>
                </Modal>
          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th>Seller Id</th>
                  <th>User Name</th>
                  <th>Mobile</th>
                  <th>Register Date</th>
                  <th>Provider</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
              {tableContent}

              </tbody>
            </table>
            <Pagination
                      activePage={this.state.currentPage}
                      itemsCountPerPage={this.state.itemsCountPerPage}
                      totalItemsCount={this.state.total}
                      pageRangeDisplayed={this.state.pageRangeDisplayed}
                      onChange={this.getData}
                      itemClass='page-item'
                      linkClass="page-link bold"
                    />
          </div>
        </div>
      </div>
    );
  }
 else {
   return(<div id="content"><Loader/></div>);
 }
  }
}

export default Authentication(SellerList);
