import React, { Component } from 'react';
import Authentication from '../Authentication/Authentication';
import {Link,Redirect} from "react-router-dom";
import {LOGIN,TAG,TOKEN,SELLER_IMAGE_LIST,USERID,HEADER,CHANGE_STATUS} from '../../url.js';
import BreadCrumb from '../../Component/breadcrumb';
import Loader from '../../Component/Loader/loader'
import axios from 'axios';
import $ from 'jquery';
import Pagination from "react-js-pagination";
class SellerImageList extends Component {
  state={
    isLoading:false,sellerList:[],
    currentPage:1,
    total:1,itemsCountPerPage:10,pageRangeDisplayed:3
}
  componentDidMount()
  {
    this.getData(1,this.state.itemsCountPerPage)
  }
  getData=(page,limit)=>{
    axios.post(`${SELLER_IMAGE_LIST}?page=${page}`,{
      'tag':'dash',
      'limit':limit,
      'user_id':USERID
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
          var response=res.data.data;
          this.setState({currentPage:response.current_page,total:response.total,sellerList:response.data});
          setTimeout(()=>this.setState({isLoading:true}),1000)
        }
    }).catch((error)=>{

    })
  }
  status=(status)=>{
    var name="";
    if(status==1)
    {
      name="Active";
    }
    if(status==2)
    {
      name="Pending";
    }
    if(status==0)
    {
      name="InActive";
    }
    if(status==3)
    {
      name="Disapproved";
    }
    if(status==4)
    {
      name="InQueue";
    }

      return( <div class="status_btn">
              <button type="button" className={`btn ${status==1?'btn-success':'btn-danger'} btn-sm`}>{name}</button>
            </div>);
  }
  changeStatus=(Id,status)=>{
    axios.post(CHANGE_STATUS,{
      'tag':'dash',
      'user_id':USERID,
      'role':'sellerimage',
      'id':Id,
      'status':status
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
    this.getData(this.state.currentPage,this.state.itemsCountPerPage)
        }
    }).catch((error)=>{

    })
  }
  render() {
    const {sellerList,isLoading}=this.state;
    const tableContent=sellerList.map((res,key)=>{
      return(<tr key={key}>
                <td><img src={res.large_thumb} style={{width:'60px'}}/></td>
                <td>{res.request_id}</td>
                <td>{res.title}</td>
                <td>{res.artist_name}</td>
                <td>{res.price}</td>
                <td>{res.created_at}</td>
                <td>{this.status(res.status)}</td>
                <td>
                <div class="dropdown">
                  <button class="btn btn-outline-secondary btn-sm dropdown-toggle" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                      Action
                  </button>
                  {res.status==4 && <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                      <a class="dropdown-item" href="javascript;;"><i class="fa fa-trash-o" aria-hidden="true"></i> Delete</a>
                  </div>}
                  {res.status==2 && <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                      <a class="dropdown-item" href="javascript:;" onClick={()=>this.changeStatus(res.id,1)}><i class="fa fa-eye" aria-hidden="true"></i>Approved</a>
                      <a class="dropdown-item" href="javascript:;" onClick={()=>this.changeStatus(res.id,3)}><i class="fa fa-pencil-square-o" aria-hidden="true" ></i>Disapproved</a>
                      <a class="dropdown-item" href="javascript;;"><i class="fa fa-trash-o" aria-hidden="true"></i> Delete</a>
                  </div>}
                  {(res.status!=2 && res.sttaus!=4) && <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                      {res.status==1 && <a class="dropdown-item" href="javascript:;" onClick={()=>this.changeStatus(res.id,0)}><i class="fa fa-eye" aria-hidden="true" ></i>Inactive</a>}
                      {res.status==0 && <a class="dropdown-item" href="javascript:;" onClick={()=>this.changeStatus(res.id,1)}><i class="fa fa-eye" aria-hidden="true" ></i>Active</a>}
                  </div>}
              </div>
                </td></tr>)
    })
    if(isLoading)
    {
    return (
      <div id="content">
        <div class="content_area">
          <BreadCrumb name="ImageList"/>
          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th>Thumburl</th>
                  <th>Image Id</th>
                  <th>Title</th>
                  <th>Artist Name</th>
                  <th>Price</th>
                  <th>Register Date</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
              {tableContent}

              </tbody>
            </table>
          </div>
          <Pagination
                    activePage={this.state.currentPage}
                    itemsCountPerPage={this.state.itemsCountPerPage}
                    totalItemsCount={this.state.total}
                    pageRangeDisplayed={this.state.pageRangeDisplayed}
                    onChange={this.getData}
                    itemClass='page-item'
                    linkClass="page-link bold"
                  />
        </div>
      </div>
    );
  }
 else {
   return(<div id="content"><Loader/></div>);
 }
  }
}

export default Authentication(SellerImageList);
