import React, { Component } from 'react';
import Authentication from '../Authentication/Authentication';
import {Link,Redirect} from "react-router-dom";
import {LOGIN,TAG,TOKEN,VENDORLIST,USERID,HEADER,CHANGE_STATUS} from '../../url.js';
import BreadCrumb from '../../Component/breadcrumb';
import axios from 'axios';
import $ from 'jquery';
import Loader from '../../Component/Loader/loader'
class VendorList extends Component {
  state={
    isLoading:false,vendorList:[],
    currentPage:1,
    total:0
}
  componentDidMount()
  {
    this.getData(1,10)
  }
  getData=(page,limit)=>{
    axios.post(VENDORLIST,{
      'tag':'dash',
      'limit':limit,
      'user_id':USERID
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
          var response=res.data.data;
          this.setState({currentPage:response.current_page,total:response.total,vendorList:response.data});
          setTimeout(()=>this.setState({isLoading:true}),1000)
        }
    }).catch((error)=>{

    })
  }
  status=(Id,status)=>{
    var name="";
    if(status==1)
    {
      name="Active";
    }
    if(status==0)
    {
      name="Inactive";
    }
      return( <div class="status_btn">
              <button type="button" className={`btn ${status==1?'btn-success':'btn-danger'} btn-sm`} id={`status_${Id}`} onClick={()=>this.changeStatus(Id,status)}>{name}</button>
            </div>);
  }
  changeStatus=(Id,status)=>{
    axios.post(CHANGE_STATUS,{
      'tag':'dash',
      'user_id':USERID,
      'role':'vendorlist',
      'id':Id,
      'status':!status
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
          var list=this.state.vendorList;
            for(var i=0;i<list.length;i++)
            {
              if(list[i].id==Id)
              {
                list[i].status=!status;
              }
            }
            this.setState({vendorList:list})
            //this.getData(1,10)
          // if(status==1)
          // {
          //   $('#status_'+Id).removeClass( "btn-success" ).addClass( "btn-danger" )
          //   $('#status_'+Id).html('Inactive');
          // }
          // if(status==0)
          // {
          //   $('#status_'+Id).removeClass( "btn-danger" ).addClass( "btn-success" );
          //   $('#status_'+Id).html('Active');
          // }
        }
    }).catch((error)=>{

    })
  }
  render() {
    const {vendorList,isLoading}=this.state;
    const tableContent=vendorList.map((res,key)=>{
      return(<tr key={key}><td><Link to={`/vendor/info/${res.id}`}>{res.Accountid}</Link></td>
                <td>{res.fname}&nbsp;{res.lname}</td>
                <td>{res.city}</td>
                <td>{res.created_at}</td>
                <td>{this.status(res.id,res.status)}</td>
                {/*<td>
                <div class="dropdown">
                  <button class="btn btn-outline-secondary btn-sm dropdown-toggle" type="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                      Active
                  </button>
                  <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                      <a class="dropdown-item" href="javascript:;"><i class="fa fa-eye" aria-hidden="true"></i> View Entries</a>
                      <a class="dropdown-item" href="javascript:;"><i class="fa fa-pencil-square-o" aria-hidden="true"></i> Edit</a>
                      <a class="dropdown-item" href="javascript;;"><i class="fa fa-trash-o" aria-hidden="true"></i> Delete</a>
                  </div>
              </div>
                </td>*/}</tr>)
    });
    if(isLoading)
    {
    return (
      <div id="content">
        <div class="content_area">
          <BreadCrumb name="VendorList"/>
          <Link to="/createVendor"><span><i class="fa fa-plus" style={{fontSize:'22px'}}></i></span></Link>
          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th>Vendor Id</th>
                  <th>User Name</th>
                  <th>City</th>
                  <th>Register Date</th>
                  <th>Status</th>
                  {/*<th>Action</th>*/}
                </tr>
              </thead>
              <tbody>
              {tableContent}

              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
 else {
   return(<div id="content"><Loader/></div>);
 }
  }
}

export default Authentication(VendorList);
