import React, { Component } from 'react';
import Authentication from '../Authentication/Authentication';
import {Link,Redirect} from "react-router-dom";
import {LOGIN,TAG,TOKEN,SELLER_VIDEO_LIST,USERID,HEADER,CHANGE_STATUS,VIDEODATA,backdropStyle,contentStyle} from '../../url.js';
import BreadCrumb from '../../Component/breadcrumb';
import Loader from '../../Component/Loader/loader'
import axios from 'axios';
import $ from 'jquery';
import Pagination from "react-js-pagination";
import  Modal from 'boron-react-modal/ScaleModal';
import ReactJWPlayer from 'react-jw-player';
class SellerVideoList extends Component {
  state={
    isLoading:false,sellerList:[],
    currentPage:1,
    total:1,itemsCountPerPage:10,pageRangeDisplayed:3,video_data:[]
}
  componentDidMount()
  {
    this.getData(1,this.state.itemsCountPerPage)
  }
  getData=(page,limit)=>{
    axios.post(`${SELLER_VIDEO_LIST}?page=${page}`,{
      'tag':'dash',
      'limit':limit,
      'user_id':USERID
    },HEADER).then((res)=>{
        if(res.data.success==1)
        {
          var response=res.data.data;
          this.setState({currentPage:response.current_page,total:response.total,sellerList:response.data})
          setTimeout(()=>this.setState({isLoading:true}),1000)
        }
    }).catch((error)=>{

    })
  }
  status=(status)=>{
    var name="";
    var btn_name="btn-success";
    if(status==1)
    {
      name="Active";
    }
    if(status==2)
    {
      name="Pending";
      btn_name="btn-primary";
    }
    if(status==0)
    {
      name="Inactive";
      btn_name="btn-danger";
    }
    if(status==3)
    {
      name="Disapproved";
      btn_name="btn-danger";
