import React from 'react'
import ReactDOM from 'react-dom';
import $ from 'jquery'
class Faq extends React.Component{
  componentDidMount()
  {
    $(".scrollTo").on('click', function(e) {
         e.preventDefault();
         var target = $(this).attr('href');
         $('html, body').animate({
           scrollTop: ($(target).offset().top)
         }, 2000);
      });
      $(window).scroll(function() {
          var height = $(window).scrollTop();
          if (height > 100) {
              $('#back2Top').fadeIn();
          } else {
              $('#back2Top').fadeOut();
          }
      });
      $(document).ready(function() {
          $("#back2Top").click(function(event) {
              event.preventDefault();
              $("html, body").animate({ scrollTop: 0 }, "slow");
              return false;
          });

      });
  }
  render()
  {
    return(
      <div class="inner_wrap">
        <div class="container">
          <div class="faq">
              <h2 class="h2 text-center">FAQs (Frequently Asked Questions)</h2>
              <div class="panel-group" id="accordion">
                <div class="panel panel-default">
                  <div class="panel-heading">
                    <h4 class="panel-title">
                      <a data-toggle="collapse" data-parent="#accordion" href="#collapse1" class="collapsed" >When was Krishna Engineering College established?</a>
                    </h4>
                  </div>
                  <div id="collapse1" class="panel-collapse collapse">
                    <div class="panel-body">
                      <p>Krishna Engineering College is one of the “Best Engineering Colleges” in Delhi / NCR region with 200+ top notch Faculty and Staff members. It was established in the year 2004 by Friends Charitable Society (FCS) and run by a group of eminent Industrialists and Professionals, with vast experience in running educational institutes like KIET, IPEC and AKGEC.</p>
                    </div>
                  </div>
                </div>

                <div class="panel panel-default">
                  <div class="panel-heading">
                    <h4 class="panel-title">
                      <a data-toggle="collapse" data-parent="#accordion" href="#collapse2" class="">Krishna Engineering College, Ghaziabad is affiliated to which university?</a>
                    </h4>
                  </div>
                  <div id="collapse2" class="panel-collapse collapse in">
                    <div class="panel-body"><p>KEC is affiliated to Dr. A.P.J. Abdul Kalam Technical University (formerly UPTU), Lucknow and is approved by All India Council for Technical Education. KEC is one among the best Engineering Colleges of Delhi / NCR.</p></div>
                  </div>
                </div>

              </div>
              <div class="clearfix"></div>
          </div>
        </div>
      </div>
    )
  }
}
export default Faq;
