                        <div class="ttl_info">
                            <div class="row">
                                <div class="col-sm-8">
                                    <h2>{response_data.name}</h2>
                                    <ul class="time_list">
                                        <li>2018</li>
                                        <li><Duration sec1={response_data.duration}/></li>
                                        <li>{response_data.SubGenre}</li>
                                        <li><span>U/A</span></li>
                                    </ul>
                                </div>
                                <div class="col-sm-4">
                                    <ul class="icon_list">
                                        <li><a href="javascript:;" onClick={()=>this.addWatchLater()}><i class="fa fa-clock-o" aria-hidden="true"></i></a></li>
                                        <li><a href="javascript:;" onClick={()=>this.addFavourites()}><img src="images/icon_view1.png" alt="" /></a></li>
                                        <li><a href="javascript:;"><img src="images/icon_view2.png" alt="" /></a></li>
                                        <li><a href="javascript:;"><img src="images/icon_view3.png" alt="" /></a></li>
                                    </ul>
                                </div>
                            </div>
                            <div class="clearfix"></div>
                        </div>
                        <div class="desc">
                            <h4>Description</h4>
                            <p>{response_data.longdescription}</p>
                            <div class="clearfix"></div>
                        </div>
                        <div class="clearfix"></div>
                    </article>
                    <div class="clearfix"></div>
                </div>
                <div class="clearfix"></div>
            </section>
            {!this.state.isRelatedLoading && <RelatedLoader/>}
          <section class="cat_gal">
                  <article class="container">
                      <div class={`horz_cat cat_slides`}>
                          <div class="slider_box">
                          {related_data.length>0 && <h2>We Recommend</h2>}
                              <OwlCarousel
                                 className="owl-theme"
                                 items={4}
                                 slideBy={4}
                                 nav={true}
                                 loop={false}
                                 mouseDrag={false}
                                 dots={false}
                                 animateOut={'fadeOut'}
                                 animateIn={'fadeIn'}
                                 autoplay={false}
                                 margin={0}
                                 navigation={true}
                                 navText={["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"]}
                                 responsive={responsive}
                                 >
                                 {Related_Video}
                                 </OwlCarousel>
                                 <div class="clearfix"></div>
                             </div>
                             <div class="clearfix"></div>
                         </div>
                     </article>
                     <div class="clearfix"></div>
                 </section>
            <div class="clearfix"></div>
        </div>)
        }
  }
  else {
    return(<Loader/>)
  }
}
}
export default VideoInfo;
