import React from 'react'
import ReactDOM from 'react-dom';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import axios from 'axios';
import {Link} from 'react-router-dom'
import Loader from '../../component/loader/home_loading';
import Content_Loader from '../../component/loader/loader';
import Horizontal_Banner from '../../component/horizontal_banner/horizontal_banner';
import Vertical_Banner from '../../component/vertical_banner/vertical_banner';

class Seasons extends React.Component{

  render()
  {
    const responsive={
      320:{items:2},
      480:{items:2},
      600:{items:3},
      960:{items:4},
      1200:{items:5}
      }
      const responsive_vertical={
        320:{items:2},
        480:{items:3},
        600:{items:5},
        960:{items:6},
        1200:{items:6}
        }
    return(
      <div>
        <section class="inner_banner">
          <div class="item">
            <img src="images/home_banner1.jpg" alt="" />
            <div class="caption">
              <div class="container">
                <div class="b_title">
                    {/*<a class="play_icon" data-toggle="modal" data-target="#homevideo_pop"><img src="images/icon_play.png" alt="" /></a>*/}
                    <h2>The Crown (season 1)</h2>
                    <p><span></span>Examples might be simplified to improve reading and basic understanding. Tutorials, references, and examples are constantly reviewed to avoid errors, but we cannot warrant full correctness of all content. While using this site, you agree to have read and accepted our terms of use,</p>
                </div>
              </div>
            </div>
          </div>
        </section>
        <section class="cat_gal">
          <article class="container">
              <div class="horz_cat cat_slides">
                  <div class="slider_box">
                      <h2>Trending Now</h2>
                      <OwlCarousel
                         className="owl-theme"
                         items={4}
                         slideBy={4}
                         nav={true}
                         loop={false}
                         mouseDrag={false}
                         dots={false}
                         animateOut={'fadeOut'}
                         animateIn={'fadeIn'}
                         autoplay={false}
                         margin={0}
                         navigation={true}
                         navText={["<i class='fa fa-angle-left'></i>","<i class='fa fa-angle-right'></i>"]}
                         responsive={responsive_vertical}

                         >
                        {props.result.search_tag.map((res,key)=>{return(<div class="item">
                              <Link to={props.result.cat_type=="cat"?`/video/${(res.name.replace(/\s/g,'')).toLowerCase()}/${res.entryid}`:`/cat/${props.route_name}/${res.name}/${res.id}`} class={` ${props.result.cat_type=="cat"?'box':''}`}>
                                  <div class="img">
                                      <img class="load_place_vert" src={props.result.cat_type=="cat"?res.thumburl.v_thumburl:res.t_custom_url}  />
                                      {props.result.cat_type=="cat" && <p class="duration"><Duration sec1={res.duration}/></p>}
                                  </div>
                                  {props.result.cat_type!="cat" && <div class="caption">
                                      <p>{res.name}</p>
                                  </div>}
                                  <div  class={` ${props.result.cat_type=="cat"?'desc':''}`}>
                                     {props.result.cat_type=="cat" && <span class="title">{res.name}</span>}
                                      {props.result.cat_type=="cat" && <span class="subtitle">{res.sub_count}</span>}
                                      {props.result.cat_type=="cat" && <div class="det">{res.description}</div>}
                                  </div>
                              </Link>
                          </div>)
                        })
                        }
                      </OwlCarousel>
                      <div class="clearfix"></div>
                  </div>
              </div>
          </article>
        </section>
      </div>
    )
  }
}
export default Seasons;
