import React from 'react'
import ReactDOM from 'react-dom';
import $ from 'jquery'
class Faq extends React.Component{
  componentDidMount()
  {
    $(".scrollTo").on('click', function(e) {
         e.preventDefault();
         var target = $(this).attr('href');
         $('html, body').animate({
           scrollTop: ($(target).offset().top)
         }, 2000);
      });
      $(window).scroll(function() {
          var height = $(window).scrollTop();
          if (height > 100) {
              $('#back2Top').fadeIn();
          } else {
              $('#back2Top').fadeOut();
          }
      });
      $(document).ready(function() {
          $("#back2Top").click(function(event) {
              event.preventDefault();
              $("html, body").animate({ scrollTop: 0 }, "slow");
              return false;
          });

      });
  }
  render()
  {
    return(
      <div class="inner_wrap">
        <div class="container">
          <div class="faq">
              <h2 class="h2 text-center">FAQs (Frequently Asked Questions)</h2>
              <div class="panel-group" id="accordion">
                <div class="panel panel-default">
                  <div class="panel-heading">
                    <h4 class="panel-title">
                      <a data-toggle="collapse" data-parent="#accordion" href="#collapse1" class="collapsed" >What is PlayFlix?</a>
                    </h4>
                  </div>
                  <div id="collapse1" class="panel-collapse collapse">
                    <div class="panel-body">
                      <p>PlayFlix app boasts of Hollywood movies in English and Hollywood dubbed movies in Hindi and in various regional languages like Marathi, Gujarati, Tamil, Kannada, Telugu, Malayalam and Bhojpuri PLAYFLIX also offers hit Korean TV Shows in Hindi PlayFlix is an app which lets you watch your favourite content in your language. We have variety of contents serving not only millennial but also family and kids We have a wide range of animated movies, series and rhymes And to pint our, we have not left the Cooking content behind For the first time ever, we have Korean series available in English and Hindi along with K-POP, Well, Even Bhojpuri moves and songs are compiled in our list too. We promise a high quality HD Content with features like creating your own playlist, screen lock while playing etc. Take a tour. PlayFlix is not just entertaining. It is more Don't miss out.</p>
                    </div>
                  </div>
                </div>

                <div class="panel panel-default">
                  <div class="panel-heading">
                    <h4 class="panel-title">
                      <a data-toggle="collapse" data-parent="#accordion" href="#collapse2" class="">How do I login to PlayFlix?</a>
                    </h4>
                  </div>
                  <div id="collapse2" class="panel-collapse collapse in">
                    <div class="panel-body"><p>If you're accessing PlayFlix on a Desktop, click on the 'Sign In' button located at the top-right corner If you're on our Mobile App. please tap the Menu icon (three horizontal bars) to go to 'Sing In' Then, enter your registered Email ID and password or login using Facebook.</p></div>
                  </div>
                </div>

                <div class="panel panel-default">
                  <div class="panel-heading">
                    <h4 class="panel-title">
                      <a data-toggle="collapse" data-parent="#accordion" href="#collapse3" class="">How do I contact Support Team?</a>
                    </h4>
                  </div>
                  <div id="collapse3" class="panel-collapse collapse">
                    <div class="panel-body"><p>To contact the Support team, go to the 'Help' section under 'Settings' or from the footer link on our website and submit a question you can also write to us directly at <a href="mailto:support@playflix.app">support@playflix.app</a>. We'll get in.</p></div>
                  </div>
                </div>

              </div>
              <div class="clearfix"></div>
          </div>
        </div>
      </div>
    )
  }
}
export default Faq;
