                              <div class="row " style={{margin:'20px 0'}}>
                                <div class="col-sm-2">
                                  <label for="preset" style={{color:'black',textTransform:'uppercase'}}>Preset</label>
                                </div>
                                <div class="col-sm-4">
                                  <Select
                                   value={this.state.selectedPreset}
                                   onChange={this.presetChange}
                                   placeholder={'Select Preset'}
                                   options={preset}
                                   isMulti={true}
                                   name="preset"
                                   id="preset"
                                   required/>
