                                   <ReactTooltip place="right" type="dark" effect="solid"/>
                                </div>
                              </div>
                              <div class="row " style={{margin:'20px 0'}}>
                                <div class="col-sm-2">
                                  <label for="priority" style={{color:'black',textTransform:'uppercase'}} >Priority</label>
                                </div>
                                <div class="col-sm-4">
                                   <Select
                                  value={this.state.selectedPriority}
                                  onChange={this.priorityChange}
                                  placeholder={'Select Priority'}
                                  options={priority}
                                  isMulti={false}
                                  name="priority"
                                  id="priority"
                                  />
                                  <a data-tip=" High - Need to be done within 5 days, Medium - within 10 days and Low - within 20 days."> <i class="fa fa-question-circle" aria-hidden="true"></i> </a>
                                  <ReactTooltip place="right" type="dark" effect="solid"/>
                                </div>
                              </div>
                              <div class="row " style={{margin:'20px 0'}}>
                                <div class="col-sm-2">
                                  <label for="input_container" style={{color:'black',textTransform:'uppercase'}}>Input Container </label>
                                </div>
                                <div class="col-sm-4">
                                  <Select
                                   value={this.state.selectedInputContainer}
                                   onChange={this.selectedInputContainerChange}
                                   placeholder={'Select Input Container'}
                                   options={inputContainer}
                                   isMulti={false}
                                   name="input_container"
                                   id="input_container"
                                   required/>
                                   <a data-tip="Please specify the video format  like mp4, 3gp."> <i class="fa fa-question-circle" aria-hidden="true"></i> </a>
                                   <ReactTooltip place="right" type="dark" effect="solid"/>
                                 </div>
                              </div>
                              <div class="row " style={{margin:'20px 0'}}>
                                <div class="col-sm-2">
                                  <label for="notification_div" style={{color:'black',textTransform:'uppercase'}}>Notification</label>
                                </div>
                                <div class="col-sm-4">
                                  <Select
                                   value={this.state.selectedNotification}
                                   onChange={this.notificationChange}
                                   placeholder={'Notification'}
                                   options={notification}
                                   isMulti={false}
                                   name="notification"
                                   id="notification_div"
                                   />
                                   <a data-tip="How to get notified."> <i class="fa fa-question-circle" aria-hidden="true"></i> </a>
                                   <ReactTooltip place="right" type="dark" effect="solid"/>
                                </div>
                              </div>
                              <div class="row " style={{margin:'20px 0'}}>
                                <div class="col-sm-2">
                                  <label for="aspect_ratio" style={{color:'black',textTransform:'uppercase'}}>Aspect Ratio</label>
                                </div>
                                <div class="col-sm-4">
                                  <Select
                                  value={this.state.selectedAspectRatio}
                                  onChange={this.selectedAspectRatioChange}
                                  placeholder={'Select Input Container'}
                                  options={aspectRatio}
                                  isMulti={false}
                                  name="aspect_ratio"
                                  id="aspect_ratio"
                                  required/>
                                  <a data-tip="It refers to the width of a video frame in relation to its height."> <i class="fa fa-question-circle" aria-hidden="true"></i> </a>
                                  <ReactTooltip place="right" type="dark" effect="solid"/>
                                </div>
                              </div>

                              <button type="submit" class="btn btn-md btn-primary pull-right mt-4 " id="save_button" style={{padding:'7px 16px 8px 16px'}}>Next</button>
                            </form>
                          </div>
                        </div>
                      </div>
                  }
                </div>
        )
}
