import React, { Component } from 'react';
import VideoList from '../../component/videolist/videolist';
import Banner from '../../container/banner/banner';
import axios from 'axios';
import {BrowserRouter as Router,Link,withRouter} from "react-router-dom";
import {VIDEO_SEARCH} from '../../url.js';
class SEARCHPAGE extends Component
{
  state={
    searchList:[],
    keyword:'',
    banner_img:'',
    searchShow:false
  }
  componentDidMount()
  {
        // console.log(this.props.location.item);
      this.searchResult(this.props.match.params.keyword);
  }
  searchResult=(searchKeyword)=>
  {
    var keyword=searchKeyword;
    this.setState({keyword:keyword});
  if(keyword=='')
  {
    this.props.history.push('/');
    return false;
  }
  axios.post(VIDEO_SEARCH,{
    searchKeyword:keyword
  })
.then(response=>{
  if(response.data.success=='1')
  {
    this.setState({searchShow:true,searchList:response.data.data})
  }
  if(response.data.success=='0')
  {
    this.setState({searchShow:false,searchList:'No Result Found'})
  }
})
.catch(function (error) {
  console.log(error);
});
  }
  componentDidUpdate()
  {
    if(this.state.keyword!=this.props.match.params.keyword)
    {
      this.searchResult(this.props.match.params.keyword);
    }
  }
  render()
  {

    const videolist = ((this.state.searchList.length>0 && this.state.searchShow )&& this.state.searchList.map((result,index)=>{
      return <VideoList video_id={result.video_id} tag="buyer_video_list" img_url={`${result.thumbnail}`} host_url={result.host_url} title={result.title} description={result.description} video_tag={result.Premium=='0'?'Free':'Premium'} price={result.Premium=='0'?'0':result.Price}/>
    }));
    return (
      <div>
  <Banner />
<br/>
<section class="features section text-center">
    <div class="container">
        <div class="features-inner section-inner has-top-divider">
            <div class="features-header text-center">
                <div class="container-sm">
                    <h6 class="section-title mt-0">Search result for <span style={{fontSize:'22px',fontFamily:'normal',color:'#0c78ce'}} >{this.state.keyword}</span></h6>
                    {this.state.searchShow && <p class="section-paragraph mb-0">Total {this.state.searchList.length} Records Found</p>}
                </div>
            </div>
            {this.state.searchShow && <div class="features-wrap">
            {videolist}
             </div>}
             {!this.state.searchShow && <div class="features-wrap">
             <h3>No Record Found</h3>
              </div> }
          </div>
        </div>
  </section></div>
      );
    }
}
export default SEARCHPAGE;
