import React from 'react';
import Loader from 'react-loader-spinner'
const CommonLoader =(props)=>{
  return(<div class="loader_cont" > <div class="load_area"><Loader
         type="RevolvingDot"
         color="#0195ED"
         height={40}
         width={40}
      ></Loader></div><h3>{props.message}</h3></div>)
}
export default CommonLoader;
